  return (
    <div>
      <ul>
        {repositories.map(repository => (
            <li key={repository.id}>
              {repository.title}
              <button onClick={() => handleRemoveRepository(1)}>
                Remover
              </button>
            </li>
        ))}
      </ul>
      <ul data-testid="repository-list">
        {repositories.map(repository => {
          <li>
          {repository.title}

          <button onClick={() => handleRemoveRepository(1)}>
            Remover
          </button>
        </li>
        })}

      </ul>

      <button onClick={handleAddRepository}>Adicionar</button>
    </div>
  );
}

export default App;
